);

const CategoryPage = ({ navigate, products, categoryName }) => {
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [priceRange, setPriceRange] = useState([0, 30000]);
    const [brands, setBrands] = useState([]);
    const [selectedBrands, setSelectedBrands] = useState([]);
    const [sortBy, setSortBy] = useState('rating_desc');

    useEffect(() => {
        const categoryProducts = products.filter(p => p.category === categoryName);
        const availableBrands = [...new Set(categoryProducts.map(p => p.brand))];
        setBrands(availableBrands);
        setSelectedBrands(availableBrands);
    }, [products, categoryName]);

    useEffect(() => {
        let tempProducts = products.filter(p => p.category === categoryName);
        
        // Wyszukiwanie
        if (searchTerm) {
            tempProducts = tempProducts.filter(p => p.name.toLowerCase().includes(searchTerm.toLowerCase()));
        }
        
        // Filtracja po cenie
        tempProducts = tempProducts.filter(p => p.price >= priceRange[0] && p.price <= priceRange[1]);
        
        // Filtracja po marce
        tempProducts = tempProducts.filter(p => selectedBrands.includes(p.brand));

        // Sortowanie
        tempProducts.sort((a, b) => {
            switch(sortBy) {
                case 'price_asc': return a.price - b.price;
                case 'price_desc': return b.price - a.price;
                case 'rating_desc': return b.rating - a.rating;
                default: return 0;
            }
        });

        setFilteredProducts(tempProducts);
    }, [products, categoryName, searchTerm, priceRange, selectedBrands, sortBy]);

    const handleBrandChange = (brand) => {
        setSelectedBrands(prev => 
            prev.includes(brand) ? prev.filter(b => b !== brand) : [...prev, brand]
        );
    };

    return (
        <div className="container mx-auto mt-24 p-4 text-white">
            <h1 className="text-4xl font-bold mb-8 text-center">{categoryName}</h1>
            <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
                {/* Panel filtrów */}
                <div className="lg:col-span-1 bg-gray-800 p-6 rounded-lg h-fit">
                    <h2 className="text-2xl font-semibold mb-6 flex items-center"><Filter className="mr-2"/>Filtry</h2>
                    
                    {/* Wyszukiwanie */}
                    <div className="mb-6">
                        <label className="block mb-2">Szukaj</label>
                        <input 
                            type="text" 
                            placeholder="Nazwa produktu..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full p-2 bg-gray-700 rounded"
                        />
                    </div>

                    {/* Zakres cen */}
                    <div className="mb-6">
                        <label className="block mb-2">Cena: {priceRange[0]} - {priceRange[1]} PLN</label>
                        <input 
                            type="range"
                            min="0"
                            max="30000"
                            step="100"
                            value={priceRange[1]}
                            onChange={(e) => setPriceRange([priceRange[0], Number(e.target.value)])}
                            className="w-full"
                        />
                    </div>
                    
                    {/* Marki */}
                    <div className="mb-6">
                        <h3 className="text-lg font-semibold mb-2">Marka</h3>
                        {brands.map(brand => (
                            <div key={brand} className="flex items-center mb-1">
                                <input type="checkbox" id={brand} checked={selectedBrands.includes(brand)} onChange={() => handleBrandChange(brand)} className="mr-2" />
                                <label htmlFor={brand}>{brand}</label>
                            </div>
                        ))}
                    </div>

                    {/* Sortowanie */}
                    <div>
                        <label className="block mb-2">Sortuj według</label>
                        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="w-full p-2 bg-gray-700 rounded">
                            <option value="rating_desc">Ocena malejąco</option>
                            <option value="price_asc">Cena rosnąco</option>
                            <option value="price_desc">Cena malejąco</option>
                        </select>
                    </div>
                </div>

                {/* Lista produktów */}
                <div className="lg:col-span-3">
                    <AnimatePresence>
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                            {filteredProducts.map(product => <ProductCard key={product.id} product={product} navigate={navigate} />)}
                        </div>
                    </AnimatePresence>
                    {filteredProducts.length === 0 && (
                        <p className="text-center text-gray-400 mt-10">Brak produktów spełniających kryteria.</p>
                    )}
                </div>
            </div>
        </div>
    );
};


const ReviewsSection = ({ productId, reviews }) => {
    const { user } = useAppContext();
    const [newReview, setNewReview] = useState({ rating: 5, text: '' });

    const handleReviewSubmit = async (e) => {
        e.preventDefault();
        if (!user) {
            alert("Musisz być zalogowany, aby dodać opinię.");
            return;
        }
        if (!newReview.text) {
            alert("Opinia nie może być pusta.");
            return;
        }
        await api.addReview(productId, {
            ...newReview,
            author: user.email,
            createdAt: new Date()
        });
        setNewReview({ rating: 5, text: '' });
    };

    return (
        <div className="mt-12">
            <h3 className="text-2xl font-bold mb-4">Opinie klientów ({reviews.length})</h3>
            <div className="space-y-6">
                {reviews.map(review => (
                    <div key={review.id} className="bg-gray-800 p-4 rounded-lg">
                        <div className="flex items-center mb-2">
                            <div className="flex">
                                {[...Array(5)].map((_, i) => <Star key={i} className={`w-5 h-5 ${i < review.rating ? 'text-yellow-400' : 'text-gray-600'}`} />)}
                            </div>
                            <p className="ml-4 font-semibold">{review.author}</p>
                        </div>
                        <p className="text-gray-300">{review.text}</p>
                    </div>
                ))}
            </div>
            
            {user && (
                <form onSubmit={handleReviewSubmit} className="mt-8 bg-gray-800 p-6 rounded-lg">
                    <h4 className="text-xl font-semibold mb-4">Dodaj swoją opinię</h4>
                    <div className="flex items-center mb-4">
                        <span>Twoja ocena:</span>
                        <div className="flex ml-4">
                            {[...Array(5)].map((_, i) => (
                                <Star 
                                    key={i} 
                                    className={`w-6 h-6 cursor-pointer ${i < newReview.rating ? 'text-yellow-400' : 'text-gray-600'}`} 
                                    onClick={() => setNewReview({...newReview, rating: i + 1})}
                                />
                            ))}
                        </div>
                    </div>
                    <textarea 
                        value={newReview.text} 
                        onChange={(e) => setNewReview({...newReview, text: e.target.value})}
                        placeholder="Twoja opinia..."
                        className="w-full p-2 bg-gray-700 rounded h-24 mb-4"
                    />
                    <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Dodaj opinię</button>
                </form>
            )}
        </div>
    );
};

const ProductPage = ({ navigate, productId }) => {
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [activeImage, setActiveImage] = useState(0);
    const { addToCart } = useAppContext();

    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            const productData = await api.fetchProduct(productId);
            setProduct(productData);
            setIsLoading(false);
        };
        fetchData();
        
        const unsubscribe = api.getReviews(productId, setReviews);
        return () => unsubscribe();
    }, [productId]);

    if (isLoading) return <LoadingScreen />;
    if (!product) return <div className="text-center mt-24 text-white">Produkt nie został znaleziony.</div>;

    return (
        <div className="container mx-auto mt-24 p-4 text-white">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                {/* Galeria zdjęć */}
                <div>
                    <div className="relative h-96 bg-gray-800 rounded-lg mb-4">
                         <img src={product.imageUrls[activeImage]} alt={product.name} className="w-full h-full object-contain" />
                    </div>
                    <div className="flex space-x-2">
                        {product.imageUrls.map((url, index) => (
                            <img 
                                key={index} 
                                src={url} 
                                alt={`${product.name} thumbnail ${index+1}`} 
                                onClick={() => setActiveImage(index)}
                                className={`w-24 h-24 object-cover rounded-md cursor-pointer border-2 ${activeImage === index ? 'border-blue-500' : 'border-transparent'}`} 
                            />
                        ))}
                    </div>
                </div>

                {/* Informacje o produkcie */}
                <div>
                    <h1 className="text-4xl font-extrabold mb-2">{product.name}</h1>
                    <p className="text-gray-400 mb-4">{product.brand} - {product.category}</p>
                    <div className="flex items-center mb-4">
                        <div className="flex">
                           {[...Array(5)].map((_, i) => <Star key={i} className={`w-5 h-5 ${i < product.rating ? 'text-yellow-400' : 'text-gray-600'}`} />)}
                        </div>
                        <span className="ml-2">({reviews.length} opinii)</span>
                    </div>
                    <p className="text-3xl font-bold text-blue-400 mb-6">{product.price} PLN</p>
                    <p className="text-gray-300 mb-6">{product.description}</p>
                    <button onClick={() => addToCart(product)} className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg flex items-center justify-center text-lg">
                        <ShoppingCart className="mr-2" /> Dodaj do koszyka
                    </button>
                </div>
            </div>
            <ReviewsSection productId={productId} reviews={reviews} />
        </div>
    );
};

const CartPage = ({ navigate }) => {
    const { cart, updateCartQuantity, removeFromCart, clearCart } = useAppContext();
    const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

    return (
        <div className="container mx-auto mt-24 p-4 text-white">
            <h1 className="text-4xl font-bold mb-8">Twój Koszyk</h1>
            {cart.length === 0 ? (
                <div className="text-center">
                    <p>Koszyk jest pusty.</p>
                    <button onClick={() => navigate('home')} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Wróć do sklepu</button>
                </div>
            ) : (
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                    <div className="lg:col-span-2 space-y-4">
                        {cart.map(item => (
                            <div key={item.id} className="flex items-center bg-gray-800 p-4 rounded-lg">
                                <img src={item.imageUrls[0]} alt={item.name} className="w-24 h-24 object-cover rounded-md mr-4" />
                                <div className="flex-grow">
                                    <h2 className="text-lg font-semibold">{item.name}</h2>
                                    <p className="text-gray-400">{item.price} PLN</p>
                                </div>
                                <div className="flex items-center">
                                    <button onClick={() => updateCartQuantity(item.id, item.quantity - 1)} disabled={item.quantity <= 1} className="p-1 bg-gray-700 rounded"><Minus/></button>
                                    <span className="w-12 text-center">{item.quantity}</span>
                                    <button onClick={() => updateCartQuantity(item.id, item.quantity + 1)} className="p-1 bg-gray-700 rounded"><Plus/></button>
                                </div>
                                <p className="w-24 text-right font-semibold">{item.price * item.quantity} PLN</p>
                                <button onClick={() => removeFromCart(item.id)} className="ml-4 text-gray-400 hover:text-red-500"><Trash2/></button>
                            </div>
                        ))}
                    </div>
                    <div className="lg:col-span-1 bg-gray-800 p-6 rounded-lg h-fit">
                        <h2 className="text-2xl font-bold mb-4">Podsumowanie</h2>
                        <div className="flex justify-between mb-2">
                            <span>Suma częściowa:</span>
                            <span>{totalPrice.toFixed(2)} PLN</span>
                        </div>
                        <div className="flex justify-between mb-4">
                            <span>Dostawa:</span>
                            <span>GRATIS</span>
                        </div>
                        <div className="flex justify-between font-bold text-xl border-t border-gray-700 pt-4 mt-4">
                            <span>Do zapłaty:</span>
                            <span>{totalPrice.toFixed(2)} PLN</span>
                        </div>
                        <button onClick={() => navigate('checkout')} className="w-full mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 rounded-lg">Przejdź do kasy</button>
                        <button onClick={clearCart} className="w-full mt-2 bg-red-600 hover:bg-red-700 text-white font-bold py-2 rounded-lg">Wyczyść koszyk</button>
                    </div>
                </div>
            )}
        </div>
    );
};

const CheckoutPage = ({ navigate }) => {
    const { cart, user, clearCart, addNotification, setOrderDetails } = useAppContext();
    const [shippingInfo, setShippingInfo] = useState({ name: '', address: '', city: '', zip: '', country: 'Polska' });
    const [currentStep, setCurrentStep] = useState(1);

    const handleChange = (e) => {
        setShippingInfo({...shippingInfo, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (currentStep === 1) {
            setCurrentStep(2);
        } else if (currentStep === 2) {
            // Logika płatności (symulacja)
            setCurrentStep(3);
            try {
                const orderData = {
                    userId: user ? user.uid : 'guest',
                    items: cart,
                    total: cart.reduce((acc, item) => acc + item.price * item.quantity, 0),
                    shippingInfo,
                    status: 'Nowe',
                    createdAt: new Date(),
                };
                const docRef = await addDoc(collection(db, 'orders'), orderData);
                setOrderDetails({ ...orderData, id: docRef.id });
                clearCart();
                addNotification("Zamówienie złożone pomyślnie!", "success");
                navigate('order-confirmation');
            } catch (error) {
                addNotification("Błąd podczas składania zamówienia.", "error");
                console.error("Błąd zapisu zamówienia:", error);
                setCurrentStep(2); // Wróć do płatności w razie błędu
            }
        }
    };
    
    const ProgressBar = ({ currentStep }) => (
        <div className="flex items-center w-full mb-8">
            <div className={`flex-1 text-center ${currentStep >= 1 ? 'text-blue-400' : 'text-gray-500'}`}>
                <div className="mx-auto w-8 h-8 rounded-full border-2 flex items-center justify-center mb-1">1</div>
                Dostawa
            </div>
            <div className={`flex-1 h-0.5 ${currentStep >= 2 ? 'bg-blue-400' : 'bg-gray-700'}`}></div>
            <div className={`flex-1 text-center ${currentStep >= 2 ? 'text-blue-400' : 'text-gray-500'}`}>
                <div className="mx-auto w-8 h-8 rounded-full border-2 flex items-center justify-center mb-1">2</div>
                Płatność
            </div>
            <div className={`flex-1 h-0.5 ${currentStep >= 3 ? 'bg-blue-400' : 'bg-gray-700'}`}></div>
            <div className={`flex-1 text-center ${currentStep >= 3 ? 'text-blue-400' : 'text-gray-500'}`}>
                <div className="mx-auto w-8 h-8 rounded-full border-2 flex items-center justify-center mb-1">3</div>
                Potwierdzenie
            </div>
        </div>
    );

    return (
        <div className="container mx-auto mt-24 p-4 text-white">
            <h1 className="text-4xl font-bold mb-8 text-center">Zamówienie</h1>
            <ProgressBar currentStep={currentStep} />
            <form onSubmit={handleSubmit} className="max-w-lg mx-auto bg-gray-800 p-8 rounded-lg">
                <AnimatePresence mode="wait">
                    {currentStep === 1 && (
                        <motion.div key="step1" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                            <h2 className="text-2xl font-semibold mb-6">Adres dostawy</h2>
                            <input name="name" value={shippingInfo.name} onChange={handleChange} placeholder="Imię i nazwisko" className="w-full p-2 bg-gray-700 rounded mb-4" required />
                            <input name="address" value={shippingInfo.address} onChange={handleChange} placeholder="Adres" className="w-full p-2 bg-gray-700 rounded mb-4" required />
                            <input name="city" value={shippingInfo.city} onChange={handleChange} placeholder="Miasto" className="w-full p-2 bg-gray-700 rounded mb-4" required />
                            <input name="zip" value={shippingInfo.zip} onChange={handleChange} placeholder="Kod pocztowy" className="w-full p-2 bg-gray-700 rounded mb-4" required />
                        </motion.div>
                    )}
                    {currentStep === 2 && (
                        <motion.div key="step2" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                            <h2 className="text-2xl font-semibold mb-6">Płatność</h2>
                            <p className="text-center text-gray-400 mb-4">Symulacja płatności. Kliknij, aby sfinalizować.</p>
                        </motion.div>
                    )}
                    {currentStep === 3 && (
                        <motion.div key="step3" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                            <h2 className="text-2xl font-semibold mb-6 text-center">Przetwarzanie...</h2>
                            <LoadingScreen />
                        </motion.div>
                    )}
                </AnimatePresence>
                 <button type="submit" className="w-full mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 rounded-lg">
                    {currentStep === 1 ? "Przejdź do płatności" : "Zapłać i zamów"}
                </button>
            </form>
        </div>
    );
};


const OrderConfirmationPage = ({ navigate, orderDetails }) => (
    <div className="container mx-auto mt-24 p-4 text-white text-center">
