const AccessDenied = ({ navigate }) => (
    <div className="container mx-auto mt-24 p-4 text-white text-center">
        <AlertCircle className="w-24 h-24 mx-auto text-red-500 mb-4" />
        <h1 className="text-4xl font-bold mb-4">Brak dostępu</h1>
        <p className="text-lg text-gray-300 mb-8">Nie masz uprawnień do przeglądania tej strony.</p>
        <button onClick={() => navigate('home')} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg">Wróć na stronę główną</button>
    </div>
);

const LoginPage = ({ navigate, addNotification }) => {
    const [credentials, setCredentials] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const { setUser } = useAppContext();

    const handleGoogleLogin = async () => {
        const provider = new GoogleAuthProvider();
        try {
            const userCredential = await signInWithPopup(auth, provider);
            const additionalUserInfo = getAdditionalUserInfo(userCredential);

            // Create user doc only if they are a new user from Google Sign-In
            if (additionalUserInfo?.isNewUser) {
                await setDoc(doc(db, 'users', userCredential.user.uid), {
                    email: userCredential.user.email,
                    role: 'user',
                    createdAt: new Date(),
                });
            }
            
            addNotification("Zalogowano pomyślnie!", "success");
            navigate('home');
        } catch (error) {
            setError("Błąd logowania przez Google.");
            addNotification("Błąd logowania przez Google.", "error");
        }
    };
    
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const userCredential = await signInWithEmailAndPassword(auth, credentials.email, credentials.password);
            setUser(userCredential.user);
            addNotification("Zalogowano pomyślnie!", "success");
            navigate('home');
        } catch (error) {
            setError('Nieprawidłowy email lub hasło.');
            addNotification("Nieprawidłowy email lub hasło.", "error");
        }
    };

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
            <div className="w-full max-w-md p-8 space-y-8 bg-gray-800 rounded-lg shadow-lg">
                <h1 className="text-3xl font-bold text-center">Logowanie</h1>
                {error && <p className="text-red-500 text-center">{error}</p>}
                <form onSubmit={handleLogin} className="space-y-6">
                    <input type="email" name="email" placeholder="Email" onChange={handleChange} className="w-full p-3 bg-gray-700 rounded" required />
                    <input type="password" name="password" placeholder="Hasło" onChange={handleChange} className="w-full p-3 bg-gray-700 rounded" required />
                    <button type="submit" className="w-full p-3 bg-blue-600 rounded font-bold hover:bg-blue-700">Zaloguj</button>
                </form>
                <div className="text-center text-gray-400">lub</div>
                 <button onClick={handleGoogleLogin} className="w-full p-3 bg-red-600 rounded font-bold hover:bg-red-700 flex items-center justify-center">
                    <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/c/c1/Google_%22G%22_logo.svg/768px-Google_%22G%22_logo.svg.png" alt="Google" className="w-5 h-5 mr-2" />
                    Zaloguj przez Google
                </button>
                <div className="text-center">
                    <a onClick={() => navigate('register')} className="font-medium text-blue-400 hover:text-blue-300 cursor-pointer">Nie masz konta? Zarejestruj się</a>
                </div>
            </div>
        </div>
    );
};

const RegisterPage = ({ navigate, addNotification }) => {
    const [credentials, setCredentials] = useState({ email: '', password: '', confirmPassword: '' });
    const [error, setError] = useState('');
    const { setUser } = useAppContext();

    const handleRegister = async (e) => {
        e.preventDefault();
        if (credentials.password !== credentials.confirmPassword) {
            setError('Hasła nie są zgodne.');
            return;
        }
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, credentials.email, credentials.password);
            
            // Ustawienie domyślnej roli 'user' dla nowego użytkownika
            await setDoc(doc(db, 'users', userCredential.user.uid), {
                email: userCredential.user.email,
                role: 'user',
                createdAt: new Date(),
            });

            setUser(userCredential.user);
            addNotification("Rejestracja pomyślna!", "success");
            navigate('home');
        } catch (error) {
            setError('Błąd rejestracji. Sprawdź, czy email jest poprawny.');
            addNotification("Błąd rejestracji.", "error");
        }
    };
    
    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
            <div className="w-full max-w-md p-8 space-y-8 bg-gray-800 rounded-lg shadow-lg">
                <h1 className="text-3xl font-bold text-center">Rejestracja</h1>
                {error && <p className="text-red-500 text-center">{error}</p>}
                <form onSubmit={handleRegister} className="space-y-6">
                    <input type="email" name="email" placeholder="Email" onChange={handleChange} className="w-full p-3 bg-gray-700 rounded" required />
                    <input type="password" name="password" placeholder="Hasło" onChange={handleChange} className="w-full p-3 bg-gray-700 rounded" required />
                    <input type="password" name="confirmPassword" placeholder="Potwierdź hasło" onChange={handleChange} className="w-full p-3 bg-gray-700 rounded" required />
                    <button type="submit" className="w-full p-3 bg-blue-600 rounded font-bold hover:bg-blue-700">Zarejestruj</button>
                </form>
                 <div className="text-center">
                    <a onClick={() => navigate('login')} className="font-medium text-blue-400 hover:text-blue-300 cursor-pointer">Masz już konto? Zaloguj się</a>
                </div>
            </div>
        </div>
    );
};


const AdminPanel = ({ navigate }) => {
    const [activeTab, setActiveTab] = useState('dashboard');
    const { userRole } = useAppContext();

    if (userRole !== 'admin') {
        return <AccessDenied navigate={navigate} />;
    }

    const renderContent = () => {
        switch (activeTab) {
            case 'dashboard': return <AdminDashboard />;
            case 'products': return <AdminProducts />;
            case 'orders': return <AdminOrders />;
            default: return <AdminDashboard />;
        }
    };

    return (
        <div className="container mx-auto mt-24 p-4 text-white">
            <h1 className="text-4xl font-bold mb-8">Panel Administratora</h1>
            <div className="flex border-b border-gray-700 mb-8">
                <button onClick={() => setActiveTab('dashboard')} className={`py-2 px-4 ${activeTab === 'dashboard' ? 'border-b-2 border-blue-500 text-blue-400' : 'text-gray-400'}`}>Dashboard</button>
                <button onClick={() => setActiveTab('products')} className={`py-2 px-4 ${activeTab === 'products' ? 'border-b-2 border-blue-500 text-blue-400' : 'text-gray-400'}`}>Produkty</button>
                <button onClick={() => setActiveTab('orders')} className={`py-2 px-4 ${activeTab === 'orders' ? 'border-b-2 border-blue-500 text-blue-400' : 'text-gray-400'}`}>Zamówienia</button>
            </div>
            <div>{renderContent()}</div>
        </div>
    );
};

const AdminDashboard = () => {
    const [stats, setStats] = useState({ products: 0, orders: 0, users: 0, revenue: 0 });

    useEffect(() => {
        const fetchStats = async () => {
            const products = await getDocs(collection(db, 'products'));
            const orders = await getDocs(collection(db, 'orders'));
            const users = await getDocs(collection(db, 'users'));
            const totalRevenue = orders.docs.reduce((acc, doc) => acc + doc.data().total, 0);
            setStats({ products: products.size, orders: orders.size, users: users.size, revenue: totalRevenue });
        };
        fetchStats();
    }, []);

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-gray-800 p-6 rounded-lg"><h3 className="text-gray-400">Produkty</h3><p className="text-3xl font-bold">{stats.products}</p></div>
            <div className="bg-gray-800 p-6 rounded-lg"><h3 className="text-gray-400">Zamówienia</h3><p className="text-3xl font-bold">{stats.orders}</p></div>
            <div className="bg-gray-800 p-6 rounded-lg"><h3 className="text-gray-400">Użytkownicy</h3><p className="text-3xl font-bold">{stats.users}</p></div>
            <div className="bg-gray-800 p-6 rounded-lg"><h3 className="text-gray-400">Przychód</h3><p className="text-3xl font-bold">{stats.revenue.toFixed(2)} PLN</p></div>
        </div>
    );
};

const AdminProducts = () => {
    const [products, setProducts] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const { addNotification } = useAppContext();

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        setIsLoading(true);
        const productsCol = query(collection(db, 'products'));
        const snapshot = await getDocs(productsCol);
        setProducts(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        setIsLoading(false);
    };

    const handleSave = async (productData) => {
        setIsLoading(true);
        try {
            // Funkcja do czyszczenia pustych wartości, które nie są obsługiwane przez Firestore
            const cleanData = (data) => {
                const cleaned = {};
                for (const key in data) {
                    if (data[key] !== null && data[key] !== undefined && data[key] !== '') {
                        cleaned[key] = data[key];
                    }
                }
                // Upewnij się, że imageUrls jest tablicą
                cleaned.imageUrls = Array.isArray(cleaned.imageUrls) ? cleaned.imageUrls : [];
                return cleaned;
            };

            const finalData = cleanData(productData);

            if (editingProduct) {
                await api.updateProduct(editingProduct.id, finalData);
                addNotification("Produkt zaktualizowany!", "success");
            } else {
                await api.addProduct(finalData);
                addNotification("Produkt dodany!", "success");
            }
            fetchProducts();
        } catch (error) {
            console.error("Błąd zapisu produktu:", error);
            addNotification(`Błąd zapisu: ${error.message}`, "error");
        }
        setIsModalOpen(false);
        setEditingProduct(null);
        setIsLoading(false);
    };
    
    const handleDelete = async (id) => {
        if (window.confirm("Czy na pewno chcesz usunąć ten produkt?")) {
            try {
                // Najpierw usuń powiązane obrazy
                const productToDelete = products.find(p => p.id === id);
                if (productToDelete && productToDelete.imageUrls) {
                    for (const url of productToDelete.imageUrls) {
                        await api.deleteImage(url);
                    }
                }
                // Następnie usuń produkt
                await api.deleteProduct(id);
                addNotification("Produkt usunięty!", "success");
                fetchProducts();
            } catch (error) {
                console.error("Błąd usuwania produktu:", error);
                addNotification(`Błąd usuwania: ${error.message}`, "error");
            }
        }
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setIsModalOpen(true);
    };
    
    const handleAddNew = () => {
        setEditingProduct(null);
        setIsModalOpen(true);
    };

    if(isLoading) return <LoadingScreen />;

    return (
        <div>
            <button onClick={handleAddNew} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4">Dodaj nowy produkt</button>
            <div className="bg-gray-800 rounded-lg overflow-hidden">
                <table className="w-full text-left">
                    <thead className="bg-gray-700">
                        <tr>
                            <th className="p-4">Nazwa</th>
                            <th className="p-4">Kategoria</th>
                            <th className="p-4">Cena</th>
                            <th className="p-4">Akcje</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id} className="border-b border-gray-700">
                                <td className="p-4">{product.name}</td>
                                <td className="p-4">{product.category}</td>
                                <td className="p-4">{product.price} PLN</td>
                                <td className="p-4">
                                    <button onClick={() => handleEdit(product)} className="text-blue-400 hover:text-blue-300 mr-2">Edytuj</button>
                                    <button onClick={() => handleDelete(product.id)} className="text-red-500 hover:text-red-400">Usuń</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {isModalOpen && <ProductEditModal product={editingProduct} onSave={handleSave} onClose={() => setIsModalOpen(false)} />}
        </div>
    );
};

const ProductEditModal = ({ product, onSave, onClose }) => {
    const [productData, setProductData] = useState(
        product || { name: '', price: '', category: '', brand: '', description: '', imageUrls: [], bestseller: false }
    );
    const [isUploading, setIsUploading] = useState(false);
    const { addNotification } = useAppContext();

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setProductData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
    };

    const handlePriceChange = (e) => {
        setProductData(prev => ({...prev, price: parseFloat(e.target.value) || 0 }));
    };

    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (file) {
            setIsUploading(true);
            try {
                const url = await api.uploadImage(file);
                setProductData(prev => ({...prev, imageUrls: [...(prev.imageUrls || []), url] }));
                addNotification("Zdjęcie dodane!", "success");
            } catch (error) {
                addNotification("Błąd przesyłania zdjęcia.", "error");
            }
            setIsUploading(false);
        }
    };
    
    const handleImageDelete = async (urlToDelete) => {
        if (window.confirm("Czy na pewno chcesz usunąć to zdjęcie?")) {
            try {
                await api.deleteImage(urlToDelete);
                setProductData(prev => ({ ...prev, imageUrls: prev.imageUrls.filter(url => url !== urlToDelete) }));
                addNotification("Zdjęcie usunięte!", "success");
            } catch (error) {
                console.error("Błąd usuwania zdjęcia:", error);
                addNotification("Błąd usuwania zdjęcia.", "error");
            }
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(productData);
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
            <div className="bg-gray-800 text-white rounded-lg p-8 w-full max-w-2xl max-h-screen overflow-y-auto">
                <h2 className="text-2xl font-bold mb-6">{product ? 'Edytuj produkt' : 'Dodaj produkt'}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input name="name" value={productData.name} onChange={handleChange} placeholder="Nazwa" className="w-full p-2 bg-gray-700 rounded" />
                    <input name="price" type="number" value={productData.price} onChange={handlePriceChange} placeholder="Cena" className="w-full p-2 bg-gray-700 rounded" />
                    <input name="category" value={productData.category} onChange={handleChange} placeholder="Kategoria" className="w-full p-2 bg-gray-700 rounded" />
                    <input name="brand" value={productData.brand} onChange={handleChange} placeholder="Marka" className="w-full p-2 bg-gray-700 rounded" />
                    <textarea name="description" value={productData.description} onChange={handleChange} placeholder="Opis" className="w-full p-2 bg-gray-700 rounded h-24" />
                    
                    <div>
                        <label className="block mb-2">Zdjęcia:</label>
                        <div className="grid grid-cols-3 gap-2 mb-2">
                           {(productData.imageUrls || []).map(url => (
                                <div key={url} className="relative group">
                                    <img src={url} alt="product" className="w-full h-24 object-cover rounded" />
                                    <button onClick={() => handleImageDelete(url)} type="button" className="absolute top-1 right-1 bg-red-600 text-white p-1 rounded-full opacity-0 group-hover:opacity-100"><Trash2 size={14} /></button>
                                </div>
                            ))}
                        </div>
                        <input type="file" onChange={handleImageUpload} className="w-full text-sm" />
                        {isUploading && <p>Przesyłanie...</p>}
                    </div>

                    <div className="flex items-center">
                        <input type="checkbox" id="bestseller" name="bestseller" checked={productData.bestseller} onChange={handleChange} className="mr-2" />
                        <label htmlFor="bestseller">Bestseller</label>
                    </div>

                    <div className="flex justify-end space-x-4 pt-4">
                        <button type="button" onClick={onClose} className="py-2 px-4 bg-gray-600 rounded">Anuluj</button>
                        <button type="submit" className="py-2 px-4 bg-blue-600 rounded">Zapisz</button>
                    </div>
                </form>
            </div>
        </div>
    );
};


const AdminOrders = () => {
    const [orders, setOrders] = useState([]);
    
    useEffect(() => {
        const fetchOrders = async () => {
            const fetchedOrders = await api.fetchOrders();
            setOrders(fetchedOrders);
        };
        fetchOrders();
    }, []);

    const handleStatusChange = async (orderId, newStatus) => {
        await api.updateOrderStatus(orderId, newStatus);
        setOrders(prev => prev.map(o => o.id === orderId ? {...o, status: newStatus} : o));
    };

    return (
        <div className="bg-gray-800 rounded-lg overflow-hidden">
            <table className="w-full text-left">
                <thead className="bg-gray-700">
                    <tr>
                        <th className="p-4">ID Zamówienia</th>
                        <th className="p-4">Klient</th>
                        <th className="p-4">Data</th>
                        <th className="p-4">Suma</th>
                        <th className="p-4">Status</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order.id} className="border-b border-gray-700">
                            <td className="p-4">{order.id.slice(0, 8)}...</td>
                            <td className="p-4">{order.shippingInfo.name}</td>
                            <td className="p-4">{new Date(order.createdAt.seconds * 1000).toLocaleDateString()}</td>
                            <td className="p-4">{order.total.toFixed(2)} PLN</td>
                            <td className="p-4">
                                <select value={order.status} onChange={(e) => handleStatusChange(order.id, e.target.value)} className="bg-gray-700 p-1 rounded">
                                    <option>Nowe</option>
                                    <option>W trakcie realizacji</option>
                                    <option>Wysłane</option>
                                    <option>Zakończone</option>
                                    <option>Anulowane</option>
                                </select>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const HeroSection = ({ navigate }) => (
    <section 
        className="relative h-screen bg-cover bg-center flex flex-col items-center justify-center text-white"
        style={{ backgroundImage: `url(${heroImageUrl})` }}
    >
        <div className="absolute inset-0 bg-black opacity-50"></div>
        <div className="relative z-10 text-center container mx-auto px-4">
            <motion.h1 
                initial={{ opacity: 0, y: -50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 1 }}
                className="text-4xl md:text-6xl font-extrabold mb-4"
            >
                Energia dla Twojej podróży
            </motion.h1>
            <motion.p 
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 1, delay: 0.5 }}
                className="text-lg md:text-xl mb-8"
            >
                Najwyższej jakości ładowarki i akcesoria do pojazdów elektrycznych.
            </motion.p>
            <motion.button 
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
